args.query:
mutation Invalidate($contract: String, $token_id: numeric) {
  update_token_metadata(
    where: {
      contract: {_eq: $contract}, 
      token_id: {_eq: $token_id}, 
      status: {_neq: "1"}, 
      expired: {_eq: true}
    }, 
    _set: {
      status: "1", 
      retry_count: "0"
    }) {
    	affected_rows
  }
}
